import React, { useState } from 'react';
import { ApolloClient, InMemoryCache, ApolloProvider, gql, useMutation, useQuery } from '@apollo/client';

const client = new ApolloClient({
    uri: 'http://localhost:5000/graphql',
    cache: new InMemoryCache(),
});

const GET_TODOS = gql `
  query {
    todos {
      id
      title
      description
      time
      image
    }
  }
`;

const ADD_TODO = gql `
  mutation AddTodo($title: String!, $description: String!, $time: String!, $image: String) {
    addTodo(title: $title, description: $description, time: $time, image: $image) {
      id
      title
      description
      time
      image
    }
  }
`;

function TodoList() {
    const { data, loading, error } = useQuery(GET_TODOS);

    if (loading) return <p > Loading... < /p>;
    if (error) return <p > Error: { error.message } < /p>;

    return ( <
        div > {
            data.todos.map(todo => ( <
                    div key = { todo.id } >
                    <
                    h3 > { todo.title } < /h3> <
                    p > { todo.description } < /p> <
                    p > { todo.time } < /p> {
                        todo.image && < img src = { todo.image }
                        alt = { todo.title }
                        />} <
                        /div>
                    ))
            } <
            /div>
        );
    }

    function TodoForm() {
        const [title, setTitle] = useState('');
        const [description, setDescription] = useState('');
        const [time, setTime] = useState('');
        const [image, setImage] = useState(null);

        const [addTodo] = useMutation(ADD_TODO);

        const handleSubmit = async(e) => {
            e.preventDefault();
            await addTodo({ variables: { title, description, time, image } });
            setTitle('');
            setDescription('');
            setTime('');
            setImage(null);
        };

        return ( <
            form onSubmit = { handleSubmit } >
            <
            input type = "text"
            placeholder = "Title"
            value = { title }
            onChange = {
                (e) => setTitle(e.target.value) }
            required /
            >
            <
            textarea placeholder = "Description"
            value = { description }
            onChange = {
                (e) => setDescription(e.target.value) }
            required /
            >
            <
            input type = "datetime-local"
            value = { time }
            onChange = {
                (e) => setTime(e.target.value) }
            required /
            >
            <
            input type = "file"
            accept = "image/*"
            onChange = {
                (e) => setImage(e.target.files[0]) }
            /> <
            button type = "submit" > Submit < /button> <
            /form>
        );
    }

    function App() {
        return ( <
            ApolloProvider client = { client } >
            <
            div >
            <
            h1 > Todo App < /h1> <
            TodoForm / >
            <
            TodoList / >
            <
            /div> <
            /ApolloProvider>
        );
    }

    export default App;
