import React, { useState, useEffect } from 'react';
import TodoList from './components/TodoList';
import TodoForm from './components/TodoForm';
import './App.css';

function App() {
    const [todos, setTodos] = useState([]);
    const [isProUser, setIsProUser] = useState(false); // Simulating Pro license
    const [loading, setLoading] = useState(false);

    // Dummy data for testing
    useEffect(() => {
        const dummyTodos = [
            { id: 1, title: "Buy groceries", description: "Milk, Eggs, Butter", time: "2025-01-20", image: "" },
            { id: 2, title: "Complete Assignment", description: "GraphQL and Flask", time: "2025-01-21", image: "" },
        ];
        setTodos(dummyTodos);
    }, []);

    const addTodo = (newTodo) => {
        setTodos([...todos, newTodo]);
    };

    const deleteTodo = (id) => {
        setTodos(todos.filter(todo => todo.id !== id));
    };

    const updateTodo = (updatedTodo) => {
        setTodos(todos.map(todo => todo.id === updatedTodo.id ? updatedTodo : todo));
    };

    return ( <
        div className = "App" >
        <
        h1 > To - Do List < /h1> <
        div >
        <
        button onClick = {
            () => setIsProUser(!isProUser) } >
        Toggle { isProUser ? "Pro" : "Free" }
        License <
        /button> <
        /div> <
        TodoForm isProUser = { isProUser }
        addTodo = { addTodo }
        /> {
            loading ? ( <
                p > Loading... < /p>
            ) : ( <
                TodoList todos = { todos }
                deleteTodo = { deleteTodo }
                updateTodo = { updateTodo }
                />
            )
        } <
        /div>
    );
}

export default App;
