import React, { useState } from 'react';
import { ApolloClient, InMemoryCache, ApolloProvider, gql, useMutation, useQuery } from '@apollo/client';
import './App.css'; // Import external CSS file

// Apollo Client setup
const client = new ApolloClient({
    uri: 'http://localhost:5000/graphql',
    cache: new InMemoryCache(),
});

// GraphQL Queries and Mutations
const GET_TODOS = gql `
  query {
    todos {
      id
      title
      description
      time
      image
    }
  }
`;

const ADD_TODO = gql `
  mutation AddTodo($title: String!, $description: String!, $time: String!, $image: String) {
    addTodo(title: $title, description: $description, time: $time, image: $image) {
      id
      title
      description
      time
      image
    }
  }
`;

// Components
function TodoList() {
    const { data, loading, error } = useQuery(GET_TODOS);

    if (loading) return <p > Loading... < /p>;
    if (error) return <p > Error: { error.message } < /p>;

    return ( <
        div className = "todo-list" > {
            data.todos.map(todo => ( <
                    div className = "todo-card"
                    key = { todo.id } >
                    <
                    h3 className = "todo-title" > { todo.title } < /h3> <
                    p className = "todo-description" > { todo.description } < /p> <
                    p className = "todo-time" > { todo.time } < /p> {
                        todo.image && < img className = "todo-image"
                        src = { todo.image }
                        alt = { todo.title }
                        />} <
                        /div>
                    ))
            } <
            /div>
        );
    }

    function TodoForm() {
        const [title, setTitle] = useState('');
        const [description, setDescription] = useState('');
        const [time, setTime] = useState('');
        const [image, setImage] = useState(null);

        const [addTodo] = useMutation(ADD_TODO);

        const handleSubmit = async(e) => {
            e.preventDefault();
            await addTodo({ variables: { title, description, time, image } });
            setTitle('');
            setDescription('');
            setTime('');
            setImage(null);
        };

        return ( <
            form className = "todo-form"
            onSubmit = { handleSubmit } >
            <
            input type = "text"
            placeholder = "Title"
            value = { title }
            onChange = {
                (e) => setTitle(e.target.value) }
            required className = "form-input" /
            >
            <
            textarea placeholder = "Description"
            value = { description }
            onChange = {
                (e) => setDescription(e.target.value) }
            required className = "form-textarea" /
            >
            <
            input type = "datetime-local"
            value = { time }
            onChange = {
                (e) => setTime(e.target.value) }
            required className = "form-input" /
            >
            <
            input type = "file"
            accept = "image/*"
            onChange = {
                (e) => setImage(e.target.files[0]) }
            className = "form-input" /
            >
            <
            button type = "submit"
            className = "form-button" > Submit < /button> <
            /form>
        );
    }

    function App() {
        return ( <
            ApolloProvider client = { client } >
            <
            div className = "app-container" >
            <
            h1 className = "app-title" > Todo App < /h1> <
            TodoForm / >
            <
            TodoList / >
            <
            /div> <
            /ApolloProvider>
        );
    }

    export default App;
